config = require('../config');

dao = require('../models/BlogDao');

log = logentries.logger(config.logToken);

exports.get = function(req, res) {
  return dao.all({
    del_status: 0
  }, {
    only: ['id', 'title', 'update_time'],
    order: ['-id']
  }, function(err, blogs) {
    if (err) {
      console.log(err);
    }
    if (err) {
      log.log("debug", err);
    }
    return res.render('index', {
      title: 'snode',
      blogs: blogs
    });
  });
};

